import { Component, OnInit } from '@angular/core';
import { UserService } from 'src/app/user/user.service';
import { Coupon } from 'src/app/component/editcoupons/editcoupons.component';
import { LoginService } from 'src/app/component/login/login.service';
import { Router } from '@angular/router';
import { NzMessageService } from 'ng-zorro-antd/message';
import { LoginStatus } from 'src/app/component/login/login.component';


export class RedemptionHistory
{
  couponId : number = 0;

	customerId: number = 0;

  vendorId : number = 0;

	redemOn : string = '';

  customerName : string = '';

  vendorName : string = '';

  couponType : string = '';

  address: string = '';

  contact: string ='';

  customerContact: string  = '';
}

@Component({
  selector: 'app-redem-history',
  templateUrl: './redem-history.component.html',
  styleUrls: ['./redem-history.component.css']
})
export class RedemHistoryComponent implements OnInit {

  constructor(private msg: NzMessageService,public userService: UserService, private loginService: LoginService, private router: Router) {

    this.getLoginDetails();
   }

   loginStatus: LoginStatus = new LoginStatus();

   getLoginDetails() {
    this.loading = true;
    this.loginService.getLoginDetails().subscribe(
      (res: any) => {
        this.loading = false;
        this.loginStatus = res;

        this.getRedemptionHistory();
      },
      (err) => {
        this.loading = false; this.msg.create('error', 'Session Expired. Please Login...');
        this.router.navigate(['login']);
      }
    );
  }

  ngOnInit(): void {

    var loginButton = window.document.getElementById("loginButton")

    if(loginButton)
    {
      loginButton.innerHTML="Logout";
      loginButton.setAttribute('href','/login');
      loginButton.setAttribute('onClick',"localStorage.removeItem('token')");
    }

    var couponsheader = window.document.getElementById("displayHeader");

    if (couponsheader) {
      couponsheader.innerHTML = 'Redemption History';
    }

  }

  loading = false;

  redemptionHistory: RedemptionHistory[] = [];
  coupon: Coupon = new Coupon();

  getRedemptionHistory()
  {
    this.loading=true;
    this.userService.getRedemptionHistory().subscribe(
      (res : any) => { console.log(res); this.redemptionHistory=res; this.loading=false;},
      (err) => { this.loading=false;}

       );
  }

  previewVisible = false;

  cancel()
  {
    this.previewVisible=false;
    this.coupon=new Coupon();
  }

  getCoupon(couponId: number)
  {
    this.loading=true;
    var formData = new FormData();
    formData.set("couponId",couponId+"");

    this.userService.getCoupon(formData).subscribe(
      (res : any) => { console.log(res); this.coupon=res; this.previewVisible=true;  this.loading=false;},
      (err) => {  this.loading=false;}

       );
  }


}
<div nz-row> &nbsp; </div>

<ng-container *ngIf="!loading && redemptionHistory && redemptionHistory.length > 0">
    <!-- <div nz-row >
        <div nz-col nzSpan="24" style='text-align: left'>

            <button nz-button nzType="primary" (click)='download()' [nzLoading]='downloading'>
                Download Excel
            </button>


        </div>
    </div> -->



</ng-container>



<div style='overflow:auto;width:100%;height:100%'>
<nz-table #basicTable nzShowPagination=false nzBordered [nzData]="['']" [nzLoading]="loading">
    <thead>
        <tr>

            <th> # </th>

            <th> Coupon Id </th>

            <ng-container *ngIf="loginStatus.userType === 'Customer'">
                <th> Outlet Id </th>
                <th> Outlet Name </th>

                <th> Address </th>
                <th> Contact </th>
                <th> Coupon Type </th>
                <th> Redemption On </th>



            </ng-container>

            <ng-container *ngIf="loginStatus.userType === 'Vendor'">
                <th> Customer Id </th>
                <th> Customer Name </th>
                <th> Contact </th>
                <th> Coupon Type </th>
                <th> Redemption On </th>








            </ng-container>





        </tr>
    </thead>

    <tbody>
        <tr *ngFor="let data of redemptionHistory; index as i">

            <td>{{ (i+1) }}</td>
            <td (click)='getCoupon(data.couponId)'><b><u style='cursor: pointer;'>{{ data.couponId }}</u></b></td>
            <ng-container *ngIf="loginStatus.userType === 'Customer'">
                <td>{{ data.vendorId }}</td>
                <td>{{ data.vendorName }}</td>
                <td>{{ data.address }}</td>
                <td>{{ data.contact }}</td>
                <td>{{ data.couponType }}</td>
                <td>{{ data.redemOn }}</td>


            </ng-container>





            <ng-container *ngIf="loginStatus.userType === 'Vendor'">

                <td>{{ data.customerId }}</td>
                <td>{{ data. customerName }}</td>
                <td> {{ data.customerContact}}</td>
                <td>{{ data.couponType }}</td>
                <td>{{ data.redemOn }}</td>

            </ng-container>






        </tr>


    </tbody>
</nz-table>
</div>
<nz-modal [nzVisible]="previewVisible" [nzContent]="modalContent" [nzFooter]="null" (nzOnCancel)="cancel()">
    <ng-template #modalContent>

        <h3> Coupon Id: {{ coupon.id }}</h3>


        <div style='overflow:auto;'>
            <div class="container" style='overflow:hidden;width:300px;height:300px;padding:0px'>
