  vendorName: string = '';

  coupon: string = '';

  expireTime: string = '';

  likeCoupon: Boolean = false;

  disLikeCoupon: Boolean = false;

  likeCount: number = 200;

  dislikeCount: number = 250;

  likeLoading: Boolean = false;

