import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { NzMessageService } from 'ng-zorro-antd/message';
import { NzTabPosition } from 'ng-zorro-antd/tabs';
import { DeviceDetectorService } from 'ngx-device-detector';
import { CustomerAccount } from 'src/app/user/account/account.component';
import { LoginStatus } from '../login/login.component';
import { LoginService } from '../login/login.service';
import { VendorService } from '../vendor/vendor.service';

@Component({
  selector: 'app-executive',
  templateUrl: './executive.component.html',
  styleUrls: ['./executive.component.css']
})
export class ExecutiveComponent implements OnInit {

  loginStatus: LoginStatus = new LoginStatus();
  loading= false;
  customerDetails: CustomerAccount[]=[];

  talign:  NzTabPosition = 'left';
  isMobile: Boolean = false;

  addSubscription()
  {
    this.router.navigate(['/subscribe']);
  }

  constructor(private vendorService: VendorService, private router: Router, private loginService: LoginService, private msg: NzMessageService, private deviceService: DeviceDetectorService) {
    const navigation = this.router.getCurrentNavigation();
    this.loginStatus =  (navigation?.extras?.state?.loginStatus); 
    if (this.loginStatus) {

      console.log(this.loginStatus);
      this.startLoading();
    }
    else {
      this.getLoginDetails();

    }


   }

   startLoading() {   

    var loginButton = window.document.getElementById("loginButton")

    if (loginButton) {
      loginButton.innerHTML = "Logout";
      loginButton.setAttribute('href', '/login');
      loginButton.setAttribute('onClick', "localStorage.removeItem('token')");
    }
  }

   getLoginDetails() {
    this.loading = true;
    this.loginService.getLoginDetails().subscribe(
      (res: any) => {
        this.loading = false;
        this.loginStatus = res;
        console.log(this.loginStatus);
        this.startLoading();

      },
      (err) => {
        this.loading = false; this.msg.create('error', 'Session Expired. Please Login...');
        this.router.navigate(['login']);
      }
    );
  }

  getExecutiveCustomerAccountDetails() {
    this.loading = true;
    this.vendorService.getExecutiveCustomerAccountDetails().subscribe(
      (res : any) => { console.log(res);   this.customerDetails = res; this.loading=false;},
      (err) => { console.log(err); this.msg.create('error','Error Occured at Server. Please try again.'); this.loading=false;}
    );
  }

  ngOnInit(): void {

        this.isMobile = this.deviceService.isMobile();

    if(this.isMobile)
    this.talign='top';

    this.getExecutiveCustomerAccountDetails();

  }



}
<div style='padding-left:2%;padding-right:2%; padding-top:2%; padding-bottom:0px;width:100%;height:90%;'>
    <div nz-row>

      <div nz-col nzSpan="24">

        <nz-tabset [nzTabPosition]="talign" nzSize="large" #tabs>


            <nz-tab nzTitle="Subscriptions">

                <h3 style='text-align: center;'> Your Subscriptions </h3>

                <button nz-button nzType="primary" (click)='tabs.nzSelectedIndex=3'>+ New User Subscription</button><br/><br/>

                <div style='overflow:auto;width:100%;height:100%'>
                <nz-table #basicTable nzShowPagination=true nzBordered [nzData]="customerDetails" [nzLoading]="loading">
                    <thead>
                        <tr>    
                            <th> # </th>    
                            <th> User Id </th>
                            <th> Name </th>
                            <th> Contact </th>
                            <th> Email </th>     
                            <th> City </th>      
                            <th> Join Date </th>    
                            <th> Login Id / ReferCode </th>
                            <th> Referer Id </th>

                            <th> Profession </th>
                            <th> Gender </th>



                        </tr>
                    </thead>  
                    <tbody>
                    <tr *ngFor="let data of basicTable.data; index as i">    
                        <td>{{ (i+1) }}</td>
                        <td>{{ data.userId }}</td>
                        <td>{{ data.name }}</td>
                        <td>{{ data.contact }}</td>
                        <td>{{ data.email }}</td>
                        <td>{{ data.city }}</td>
                        <td>{{ data.joinDate }}</td>
                        <td>{{ data.referCode }}</td>
                        <td>{{ data.refererId }}</td>


                        <td>{{ data.profession }}</td>
                        <td>{{ data.gender }}</td>








                    </tr>


                </tbody>
                </nz-table>
           </div>


            </nz-tab>

                <nz-tab nzTitle="Wallet">
                     <h3 style='text-align: center;'>
                    Your Wallet Amount is 0.00 <br>
                    Your Credited Amount is 0.00
                    </h3>
                </nz-tab>
                <nz-tab nzTitle="Account">
                    <app-vendoraccount [loginStatus]="loginStatus"></app-vendoraccount>
                </nz-tab>
                <nz-tab nzTitle="New Subscription">
                    <app-subscription [executiveId]="loginStatus.loginId"></app-subscription>
                </nz-tab>
                </nz-tabset>
                </div>
                </div>
</div>
