import { Component, OnInit } from '@angular/core';
import { VendorDetails } from 'src/app/component/vendoraccount/vendoraccount.component';
import { AdminService } from '../admin.service';
import { NzModalService } from 'ng-zorro-antd/modal';
import { NzMessageService } from 'ng-zorro-antd/message';

@Component({
  selector: 'app-designers',
  templateUrl: './designers.component.html',
  styleUrls: ['./designers.component.css']
})
export class DesignersComponent implements OnInit {

  constructor(public adminService:AdminService,private modal: NzModalService, private msg: NzMessageService) { }


  sortDId = (a: VendorDetails, b: VendorDetails) => a.vendorId - b.vendorId;
  sortDName = (a: VendorDetails, b: VendorDetails) => a.name.localeCompare(b.name);
  sortDCity = (a: VendorDetails, b: VendorDetails) => a.city.localeCompare(b.city);

  ngOnInit(): void {
    this.getDesigners();
  }

  loading=false;
  designers: VendorDetails[]=[];
  previewDesignerVisible=false;
  editVisible=false;
  name: string='';
  contact: string='';
  email: string='';
  city: string = '';
  editId: number = 0;



  getDesigners()
  {
    this.loading = true;
    this.adminService.getDesigners().subscribe(

      (res:any) => { console.log(res);this.designers = res; this.loading = false; },
      (err) => { this.loading = false; }

    );
  }

  editDesigner()
  {
    if (this.name.trim().length < 3) {
      this.msg.create("error", "Name should be minimum of 3 Characters");
      return;
    }

    else if (!this.adminService.isContactNumber(this.contact)) {
      this.msg.create("error", "Contact Number should be 10 Digits");
      return;
    }

    else if(this.email.trim().indexOf('@')==-1 || this.email.trim().indexOf('@') != this.email.trim().lastIndexOf('@') || this.email.trim().lastIndexOf('@') > this.email.trim().lastIndexOf('.')  || this.email.trim().endsWith('.') || this.email.trim().startsWith('@')  || this.email.trim().endsWith('@') || this.email.trim().startsWith('.') || this.email.trim().indexOf('.')==-1 )
    {
      this.msg.create("error", "Invalid Email Address..");
      return;
    }


    else if (this.city === '' || !this.city) {
      this.msg.create("error", "Please Select City");
      return;
    }

    this.loading = true;
    this.previewDesignerVisible = false;


    let designer = new VendorDetails();
    designer.vendorId=this.editId;
    designer.name=this.name;
    designer.contact=this.contact;
    designer.email=this.email;
    designer.city=this.city;

    this.adminService.editDesigner(designer).subscribe(
      (res) => { console.log(res); this.loading = false; if (res) { this.msg.create('success', 'Designer Details Modified.'); this.ngOnInit(); } },
      (err) => { console.log(err); this.loading = false; this.msg.create('error', 'Error Occured while Modifiing Designer'); }
    )

  }


  edit(vendor: VendorDetails)
  {
    this.name=vendor.name;
    this.contact=vendor.contact;
    this.email=vendor.email;
    this.city=vendor.city;
    this.editId=vendor.vendorId;
  }

  showConfirm(text: string, editId: number): void {
    this.modal.confirm({
      nzTitle: 'Are you sure ' + text  + '?',
      nzOkText: 'Yes',
      nzOkType: 'primary',
      nzOkDanger: true,
      nzOnOk: () => {
        if (text === 'Delete') {
         this.deleteDesigner(editId);


        }
        else if (text === 'Disable' || text === 'Enable') {

          this.disableDesigner(editId);
        }
      },
      nzCancelText: 'No',
      nzOnCancel: () => console.log('Cancel')
    });

  }

  deleteDesigner(editId: number)
  {
    this.loading = true;
    var formData = new FormData();
    formData.set("vendorId",editId+"");
    this.adminService.deleteDesigner(formData).subscribe(

      (res:any) => { console.log(res); if(res) this.ngOnInit(); this.loading = false; },
      (err) => { this.loading = false; }

    );
  }

  disableDesigner(editId: number)
  {
    this.loading = true;
    var formData = new FormData();
    formData.set("vendorId",editId+"");
    this.adminService.disableDesigner(formData).subscribe(

      (res:any) => { console.log(res);if(res) this.ngOnInit(); this.loading = false; },
      (err) => { this.loading = false; }

    );
  }


  addDesignerView()
  {

    this.name='';
    this.contact='';
    this.email='';
    this.city='';
    this.editVisible=false;
    this.previewDesignerVisible = true;

  }

  addDesigner()
  {
    if (this.name.trim().length < 3) {
      this.msg.create("error", "Name should be minimum of 3 Characters");
      return;
    }

    else if (!this.adminService.isContactNumber(this.contact)) {
      this.msg.create("error", "Contact Number should be 10 Digits");
      return;
    }

    else if(this.email.trim().indexOf('@')==-1 || this.email.trim().indexOf('@') != this.email.trim().lastIndexOf('@') || this.email.trim().lastIndexOf('@') > this.email.trim().lastIndexOf('.')  || this.email.trim().endsWith('.') || this.email.trim().startsWith('@')  || this.email.trim().endsWith('@') || this.email.trim().startsWith('.') || this.email.trim().indexOf('.')==-1 )
    {
      this.msg.create("error", "Invalid Email Address..");
      return;
    }


    else if (this.city === '' || !this.city) {
      this.msg.create("error", "Please Select City");
      return;
    }


    this.loading = true;
    this.previewDesignerVisible = false;


    let designer = new VendorDetails();
    designer.name=this.name;
    designer.contact=this.contact;
    designer.email=this.email;
    designer.city=this.city;

    this.adminService.addDesigner(designer).subscribe(
      (res) => { console.log(res); this.loading = false; if (res) { this.msg.create('success', 'Designer Created.'); this.ngOnInit(); } },
      (err) => { console.log(err); this.loading = false; this.msg.create('error', 'Error Occured while creating Designer'); }
    )

  }

}
<button nz-button nzType="primary" (click)='addDesignerView()'>+ Add Designer</button>
<div nz-row> &nbsp; </div>
<div style='overflow:auto;width:100%;height:100%'>
    <nz-table #basicTable nzShowPagination=true nzBordered [nzData]="designers" [nzLoading]="loading">
        <thead>
            <tr>

                <th [nzSortFn]="sortDId">Id</th>
                <th [nzSortFn]="sortDName">Name</th>
                    <th>Contact Number</th>
                    <th>Email</th>
                    <th [nzSortFn]="sortDCity">City</th>
                    <th>Action</th>

            </tr>
        </thead>
        <tbody>
            <tr *ngFor="let data of basicTable.data; index as i">

                <td>{{ data.vendorId }}</td>
                <td>{{ data.name }}</td>
                <td>{{ data.contact }}</td>
                <td>{{ data.email }}</td>
                <td>{{ data.city }}</td>
                <td>
                    <i nz-icon [nzType]="data.isDisabled?'eye-invisible': 'eye'" nzTheme="outline"
                (click)="showConfirm(data.isDisabled?'Enable':'Disable',data.vendorId )"></i> &nbsp;
                    <i nz-icon nzType="edit" nzTheme="outline"
                      (click)="edit(data);previewDesignerVisible=true;editVisible=true"></i>
                    &nbsp;
                    <i nz-icon nzType="delete" nzTheme="outline" (click)="showConfirm('Delete',data.vendorId )"></i>
                  </td>

            </tr>
        </tbody>
    </nz-table>
</div>

<nz-modal [nzVisible]="previewDesignerVisible" [nzContent]="modalContent" [nzFooter]="null"
    (nzOnCancel)="previewDesignerVisible = false;editVisible=false;">
    <ng-template #modalContent>
        <h3> Designer </h3>
        <div nz-row class="container">
            <div nz-col nzSpan="8" style='text-align: left'>Name</div>
            <div nz-col nzSpan="4" style='text-align: right'>
            <ng-container *ngIf="name.trim().length > 0">
                <i nz-icon [nzType]="name.trim().length> 5 ? 'check' : 'close'" nzTheme="outline"></i> &nbsp;&nbsp;
               </ng-container>
            </div>
            <div nz-col nzSpan="12" style='text-align: left'>
                <input type="text" nz-input placeholder="Enter Name" style='width:100%'
                [(ngModel)]='name' />
            </div>
        </div>

        <div nz-row> &nbsp; </div>

        <div nz-row class="container">
            <div nz-col nzSpan="8" style='text-align: left'>Contact Number</div>
            <div nz-col nzSpan="4" style='text-align: right'>
            <ng-container *ngIf="contact.trim().length > 0">
                <i nz-icon [nzType]="adminService.isContactNumber(contact) ?'check' : 'close'" nzTheme="outline"></i> &nbsp;&nbsp;
               </ng-container>
            </div>

            <div nz-col nzSpan="12" style='text-align: left'>
                <input type="text" nz-input placeholder="Enter Contact Number" style='width:100%'
                [(ngModel)]='contact' />
            </div>
        </div>

        <div nz-row> &nbsp; </div>

        <div nz-row class="container">
            <div nz-col nzSpan="8" style='text-align: left'>Email Address</div>
            <div nz-col nzSpan="4" style='text-align: right'>
            <ng-container *ngIf="email.trim().length > 0">
                <i nz-icon [nzType]="email.trim().indexOf('@')==-1 || email.trim().indexOf('@') != email.trim().lastIndexOf('@') || email.trim().lastIndexOf('@') > email.trim().lastIndexOf('.')  || email.trim().endsWith('.') || email.trim().startsWith('@')  || email.trim().endsWith('@') || email.trim().startsWith('.') || email.trim().indexOf('.')==-1 ?'close' : 'check'" nzTheme="outline"></i> &nbsp;&nbsp;
               </ng-container>
            </div>

            <div nz-col nzSpan="12" style='text-align: left'>
                <input type="text" nz-input placeholder="Enter Email Address" style='width:100%'
                [(ngModel)]='email' />
            </div>
        </div>

        <div nz-row> &nbsp; </div>

        <div nz-row class="container">
            <div nz-col nzSpan="8" style='text-align: left'>City</div>
            <div nz-col nzSpan="4" style='text-align: right'>
            <ng-container *ngIf="city.trim().length > 0">
                <i nz-icon [nzType]="city.trim().length > 0 ?'check' : 'close'" nzTheme="outline"></i> &nbsp;&nbsp;
               </ng-container>
            </div>
            <div nz-col nzSpan="12" style='text-align: left'>
                <nz-select  [(ngModel)]="city" nzPlaceHolder="Select City" style='width:100%'>
                    <nz-option nzValue="Tirupati" nzLabel="Tirupati"></nz-option>
                    <nz-option nzValue="Chittoor" nzLabel="Chittoor"></nz-option>
                    <nz-option nzValue="Ananthapur" nzLabel="Ananthapur"></nz-option>
                    <nz-option nzValue="Guntur" nzLabel="Guntur"></nz-option>
                    <nz-option nzValue="Kadapa" nzLabel="Kadapa"></nz-option>
                    <nz-option nzValue="Kakinada" nzLabel="Kakinada"></nz-option>
                    <nz-option nzValue="Kurnool" nzLabel="Kurnool"></nz-option>
                    <nz-option nzValue="Nellore" nzLabel="Nellore"></nz-option>
                    <nz-option nzValue="Guntur" nzLabel="Guntur"></nz-option>
                    <nz-option nzValue="Ongole" nzLabel="Ongole"></nz-option>
                    <nz-option nzValue="Vijayawada" nzLabel="Vijayawada"></nz-option>



                  </nz-select>
            </div>
        </div>
        <div nz-row> &nbsp; </div>
        <div nz-row class="container">
            <div nz-col nzSpan="24" style='text-align: right'>
                <ng-container *ngIf="!editVisible">
                <button nz-button nzType="primary" (click)='addDesigner()'>Add</button>
            </ng-container>
            <ng-container *ngIf="editVisible">
                <button nz-button nzType="primary" (click)='editDesigner()'>Edit</button>
            </ng-container>
            </div>
        </div>
    </ng-template>
</nz-modal>
