import { Component, OnInit } from '@angular/core';
import { NzMessageService } from 'ng-zorro-antd/message';
import { AdminService } from '../admin.service';

export class StatsDTO
{
  roleName: string = '';
  todayCount: number = 0;
  monthCount: number = 0;
  totalCount: number = 0;
    type: string = '';
}

@Component({
  selector: 'app-stats',
  templateUrl: './stats.component.html',
  styleUrls: ['./stats.component.css']
})
export class StatsComponent implements OnInit {

  stats : StatsDTO[] = [];
  loading = false;
  walletBalance: string = '';
  constructor(private adminService: AdminService, private msg: NzMessageService) { }

  ngOnInit(): void {

    this.getUserStats();
    this.getWalletBalance();
  }

  getUserStats()
  {
    this.loading=true;
    this.adminService.getUserStats().subscribe(
      (res : any) => { console.log(res);   this.stats = res; this.loading=false;},
      (err) => { console.log(err); this.msg.create('error','Error Occured at Server. Please try again.'); this.loading=false;}

       );
  }

  getWalletBalance()
  {
    this.adminService.getWalletBalance().subscribe(
      (res : any) => { console.log(res);   this.walletBalance = res.walletBalance; },
      (err) => { console.log(err); this.msg.create('error','Error Occured at Server. Please try again.');}

       );
  }

}
<div nz-row class="container">
    <div nz-col nzSpan="24" style='text-align: Center'><h3>Wallet Balance - {{walletBalance}}</h3></div>
    <div nz-col nzSpan="24">&nbsp;</div>
</div>
<div style='overflow:auto;width:100%;height:100%'>
<nz-table #basicTable nzShowPagination=false nzBordered [nzData]="['']" [nzLoading]="loading">
    <thead>
        <tr>
            <th> # </th>
            <th> Role Name </th>
          <th> Type </th>
            <th> Today's Count </th>
            <th> This Month Count </th>
            <th> Total </th>


        </tr>
    </thead>
    <tbody>
        <tr *ngFor="let data of stats; index as i">
            <td>{{ (i+1) }}</td>
            <td>{{ data.roleName }}</td>
           <td>{{ data.type }}</td>
            <td>{{ data.todayCount }}</td>
            <td>{{ data.monthCount }}</td>
            <td>{{ data.totalCount }}</td>
        </tr>


    </tbody>
</nz-table>
 </div>
